SET SQL_MODE = "NO_AUTO_VALUE_ON_ZERO";
SET time_zone = "+00:00";

CREATE TABLE IF NOT EXISTS `pic_vote` (
  `vote_uid` varchar(10) NOT NULL,
  `vote_items` varchar(400) NOT NULL,
  UNIQUE KEY `vote_openid` (`vote_uid`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='军训摄影大赛微信投票统计';

CREATE TABLE IF NOT EXISTS `wechat_pic_url` (
  `pic_id` int(11) NOT NULL AUTO_INCREMENT,
  `pic_author_openid` varchar(100) NOT NULL,
  `pic_url` text NOT NULL,
  `pic_author` varchar(50) NOT NULL,
  `pic_describe` text,
  `pic_deleted` int(11) NOT NULL DEFAULT '0',
  PRIMARY KEY (`pic_id`),
  UNIQUE KEY `pic_author_openid` (`pic_author_openid`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=160 ;

